'use strict';

function initMenu() {
    var a = document.querySelectorAll('.menu-burger');
    [].forEach.call(a, function(c) {
        var d = document.querySelector(c.dataset.target);
        d && c.addEventListener('click', function(f) { f.preventDefault(), d.classList.toggle('is-active') })
    });
    var b = document.querySelectorAll('.menu');
    [].forEach.call(b, function(c) {
        var d = c.querySelectorAll('.menu-item:not(.has-dropdown).is-active');
        [].forEach.call(d, function(g) { g = g.closest('.menu-item.has-dropdown'), g && g.classList.add('is-active') });
        var f = c.querySelectorAll('.menu-item.has-dropdown > .menu-title');
        [].forEach.call(f, function(g) {
            g.addEventListener('click', function(h) {
                h.preventDefault();
                var i = h.currentTarget;
                i.closest('.menu-item.has-dropdown').classList.toggle('is-active');
                var j = c.querySelectorAll('.menu-item.has-dropdown.is-active');
                [].forEach.call(j, function(k) { i.isEqualNode(k.querySelector('.menu-title')) || k.classList.remove('is-active') })
            })
        })
    })
}